// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.7.0 <0.9.0;
import "hardhat/console.sol";

// max txn fee
// custom gas price
// max gas price
contract Chaining {
    // function chainCall(address[] memory targets, bytes[] memory callDatas)
    function chainCall(address target, bytes memory callData)
        external
        returns (bool, bytes memory)
    {
        uint256 gasStart = gasleft();
        (bool success, bytes memory returnData) = target.call(callData);

        uint256 y = 0;
        for (uint256 x = 0; x < 100; x++) {
            y += 1;
        }

        uint256 gasUsed = gasStart - gasleft();
        console.log("GAS USED: ", gasUsed);

        (bool canExec, bytes memory payload) = abi.decode(
            returnData,
            (bool, bytes)
        );

        return (canExec, payload);
    }
}

contract MockResolver {
    struct GasSettings {
        uint256 maxFee;
        uint256 gasPrice;
    }

    function newChecker()
        external
        pure
        returns (
            GasSettings memory feeSettings,
            bool canExec,
            bytes memory payload
        )
    {
        feeSettings = GasSettings(3 ether, 95 gwei);
        return (feeSettings, true, bytes("Can Exec"));
    }

    function checker()
        external
        pure
        returns (bool canExec, bytes memory payload)
    {
        canExec = true;
        payload = bytes("Can Exec");
        //0x43616e2045786563
    }
}

