contracts/UserInfoMeta.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

import {
    EIP712
} from "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import {
    ERC2771Context
} from "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "hardhat/console.sol";

contract UserInfoMeta is EIP712, ERC2771Context {
    using ECDSA for bytes32;

    struct UserInfo {
        uint256 age;
        address wallet;
        string name;
    }

    bytes32 private constant _TYPEHASH =
        keccak256("UserInfo(uint256 age,address wallet)");

    mapping(address => UserInfo) public userInfo;

    constructor(address _relayer)
        ERC2771Context(_relayer)
        EIP712("UserInfoMeta", "0.0.1")
    {}

    function updateUserInfo(
        UserInfo calldata _userInfo,
        bytes calldata signature
    ) external {
        require(isTrustedForwarder(msg.sender), "Only relayer");
        verify(_userInfo, signature);
        console.log("_updateUserInfo : sender : ", msg.sender);
    }

    function _updateUserInfo(address _user, UserInfo calldata _userInfo)
        internal
    {
        userInfo[_user] = _userInfo;
    }

    function verify(UserInfo calldata _userInfo, bytes calldata _signature)
        public
        view
        returns (bool)
    {
        address signer = _hashTypedDataV4(
            // if bytes, you have to keccak256()
            // string does not work for now
            keccak256(
                abi.encode(
                    _TYPEHASH,
                    _userInfo.age,
                    _userInfo.wallet,
                    keccak256(bytes(_userInfo.name))
                )
            )
        ).recover(_signature);

        console.log("Signer of data: ", signer);

        return true;
    }
}

