contracts/QiBond/SmolApe.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

import {
    IERC20,
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

interface IRouter {
    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
        external
        returns (
            uint256 amountA,
            uint256 amountB,
            uint256 liquidity
        );

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) external view returns (uint256 amountB);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);
}

interface IQiBond {
    function deposit(
        uint256 _amount,
        uint256 _maxPrice,
        address _depositor
    ) external returns (uint256);
}

interface IPair {
    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );
}

contract SmolApe is Ownable {
    using SafeERC20 for IERC20;

    address public constant POKE_ME =
        address(0x527a819db1eb0e34426297b03bae11F2f8B3A19E);
    address public constant ROUTER =
        address(0xa5E0829CaCEd8fFDD4De3c43696c57F7D7A678ff);
    address public constant QI_BOND =
        address(0xb3639869BC724dec8e6AFbAe480d0cD564CaD0C8);
    address public constant QI =
        address(0x580A84C73811E1839F75d86d75d88cCa0c241fF4); // token0
    address public constant DAI =
        address(0x8f3Cf7ad23Cd3CaDbD9735AFf958023239c6A063); // token1
    address public constant QI_DAI_PAIR =
        address(0xe7519Be0E2A4450815858343ca480d1939bE7281);

    constructor() {
        _approvals();
    }

    function ape(
        uint256 _amountDai,
        address _buyer,
        uint256 _maxPrice
    ) external {
        require(msg.sender == POKE_ME, "Only Poke Me");

        _receiveDai(_amountDai, _buyer);
        // swap 50% DAI to QI
        _swapDaiToQi(_amountDai / 2);
        _provideLiquidity();
        _buyBond(_buyer, _maxPrice);
        _returnFunds(_buyer);
    }

    function _approvals() internal {
        IERC20(DAI).approve(ROUTER, 2**256 - 1);
        IERC20(QI).approve(ROUTER, 2**256 - 1);
        IERC20(QI_DAI_PAIR).approve(ROUTER, 2**256 - 1);
        IERC20(QI_DAI_PAIR).approve(QI_BOND, 2**256 - 1);
    }

    function _receiveDai(uint256 _amount, address _buyer) internal {
        IERC20(DAI).safeTransferFrom(_buyer, address(this), _amount);
    }

    function _swapDaiToQi(uint256 _amountDai) internal {
        address[] memory path = new address[](2);
        path[0] = DAI;
        path[1] = QI;

        (uint112 reserveQI, uint112 reserveDAI, ) = IPair(QI_DAI_PAIR)
            .getReserves();

        // 2 % slippage
        uint256 amountOutMin = ((
            IRouter(ROUTER).getAmountOut(_amountDai, reserveDAI, reserveQI)
        ) * 98) / 100;

        IRouter(ROUTER).swapExactTokensForTokens(
            _amountDai,
            amountOutMin,
            path,
            address(this),
            block.timestamp + 120
        );
    }

    function _provideLiquidity() internal {
        (uint112 reserveQI, uint112 reserveDAI, ) = IPair(QI_DAI_PAIR)
            .getReserves();

        // use 99% just in case
        uint256 desiredDAI = (IERC20(DAI).balanceOf(address(this)) * 99) / 100;
        uint256 desiredQI = IRouter(ROUTER).quote(
            desiredDAI,
            reserveDAI,
            reserveQI
        );

        IRouter(ROUTER).addLiquidity(
            DAI,
            QI,
            desiredDAI,
            desiredQI,
            (desiredDAI * 98) / 100,
            (desiredQI * 98) / 100,
            address(this),
            block.timestamp + 120
        );
    }

    function _buyBond(address _buyer, uint256 _maxPrice) internal {
        uint256 qiDaiAmount = IERC20(QI_DAI_PAIR).balanceOf(address(this));

        IQiBond(QI_BOND).deposit(qiDaiAmount, _maxPrice, _buyer);
    }

    function _returnFunds(address _buyer) internal {
        uint256 daiBalance = IERC20(DAI).balanceOf(address(this));
        uint256 qiBalance = IERC20(QI).balanceOf(address(this));
        uint256 qiDaiBalance = IERC20(QI_DAI_PAIR).balanceOf(address(this));

        if (daiBalance > 0) IERC20(DAI).safeTransfer(_buyer, daiBalance);
        if (qiBalance > 0) IERC20(QI).safeTransfer(_buyer, qiBalance);
        if (qiDaiBalance > 0)
            IERC20(QI_DAI_PAIR).safeTransfer(_buyer, qiDaiBalance);
    }

    // --------- rug pull --------------- //

    function yoink(address _token) external onlyOwner {
        address owner = owner();
        uint256 tokenBalance = IERC20(_token).balanceOf(address(this));

        IERC20(_token).safeTransfer(owner, tokenBalance);
    }
}

contracts/QiBond/Wallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

import {
    IERC20,
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract Wallet is Ownable {
    using SafeERC20 for IERC20;
    address public constant DAI =
        address(0x8f3Cf7ad23Cd3CaDbD9735AFf958023239c6A063); // token1
    address public constant QI =
        address(0x580A84C73811E1839F75d86d75d88cCa0c241fF4); // token0
    address public constant QI_DAI_PAIR =
        address(0xe7519Be0E2A4450815858343ca480d1939bE7281);

    function returnFunds(address _buyer) external onlyOwner {
        uint256 daiBalance = IERC20(DAI).balanceOf(address(this));
        uint256 qiBalance = IERC20(QI).balanceOf(address(this));
        uint256 qiDaiBalance = IERC20(QI_DAI_PAIR).balanceOf(address(this));

        if (daiBalance > 0) IERC20(DAI).safeTransfer(_buyer, daiBalance);
        if (qiBalance > 0) IERC20(QI).safeTransfer(_buyer, qiBalance);
        if (qiDaiBalance > 0)
            IERC20(QI_DAI_PAIR).safeTransfer(_buyer, qiDaiBalance);
    }

    function withdrawERC20(address _token) external onlyOwner {
        address owner = owner();
        uint256 tokenBalance = IERC20(_token).balanceOf(address(this));

        IERC20(_token).safeTransfer(owner, tokenBalance);
    }

    function callPayload(address _dest, bytes memory _payload)
        external
        onlyOwner
    {
        _dest.call(_payload);
    }

    function approveERC20(
        address _token,
        address _spender,
        uint256 _amount
    ) external onlyOwner {
        IERC20(_token).approve(_spender, _amount);
    }
}

