        address(0x580A84C73811E1839F75d86d75d88cCa0c241fF4); // token0
    address public constant DAI =
        address(0x8f3Cf7ad23Cd3CaDbD9735AFf958023239c6A063); // token1
    address public constant QI_DAI_PAIR =
        address(0xe7519Be0E2A4450815858343ca480d1939bE7281);

    constructor() {
        _approvals();
    }

    function ape(
        uint256 _amountDai,
        address _buyer,
        uint256 _maxPrice
    ) external {
        require(msg.sender == POKE_ME, "Only Poke Me");
