        IERC20(QI).approve(ROUTER, 2**256 - 1);
        IERC20(QI_DAI_PAIR).approve(ROUTER, 2**256 - 1);
        IERC20(QI_DAI_PAIR).approve(QI_BOND, 2**256 - 1);
    }

    function _receiveDai(uint256 _amount, address _buyer) internal {
        IERC20(DAI).safeTransferFrom(_buyer, address(this), _amount);
    }

    function _swapDaiToQi(uint256 _amountDai) internal {
        address[] memory path = new address[](2);
        path[0] = DAI;
        path[1] = QI;

        (uint112 reserveQI, uint112 reserveDAI, ) = IPair(QI_DAI_PAIR)
            .getReserves();

        // 2 % slippage
        uint256 amountOutMin = ((
            IRouter(ROUTER).getAmountOut(_amountDai, reserveDAI, reserveQI)
        ) * 98) / 100;

        IRouter(ROUTER).swapExactTokensForTokens(
            _amountDai,
            amountOutMin,
            path,
            address(this),
            block.timestamp + 120
        );
