        public
        view
        returns (uint256 lpAmount)
    {
        address[] memory path = new address[](2);
        path[0] = DAI;
        path[1] = QI;

        (uint112 reserveQI, uint112 reserveDAI, ) = IPair(QI_DAI_PAIR)
